function pageprocessorLookupClasses(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.selectedSubject = "";
    $scope.courseNumber = "";
    $scope.partTermCode = "";
    $scope.selectedInstructor = "";
    $scope.courseTitle = "";
    $scope.creditFrom = "";
    $scope.creditTo = "";
    $scope.selectedvalue = "";

    var termdropdownURL = "/services/student/bannertermsForClassLookup";
    $rootScope.getAPI($rootScope.middlewareServerUrl + termdropdownURL, function (data) {
        $scope.termdropdown = data.termdropdown;
    });

    $scope.dropdownselect = function (selectedvalue) {
        var otherSearchParamsURL = "/services/student/bannergetOtherClassSearchParams/termCode=";
        $rootScope.getAPI($rootScope.middlewareServerUrl + otherSearchParamsURL + $scope.selectedvalue, function (data) {
            $scope.listdata = data;
        });
    }

    $scope.search = function () {
        if ($scope.selectedvalue == "") {
            if (window.device) {
                alert("Please select term code");
            } else {
                apprise("Please select term code", {
                    'verify': false,
                    'textYes': "Ok"
                }, function (r) {

                });
            }
            return;
        }
        if ($scope.selectedSubject == "" && $scope.selectedInstructor == "") {
            if (window.device) {
                alert("Please select Subject or Instructor");
            } else {
                apprise("Please select Subject or Instructor", {
                    'verify': false,
                    'textYes': "Ok"
                }, function (r) {

                });
            }
            return;
        }
        var searchURL = "/services/student/bannerclasslookup/termCode=" + $scope.selectedvalue +
            "&subjectCode=" + $scope.selectedSubject +
            "&courseNumber=" + $scope.courseNumber +
            "&partTermCode=" + $scope.partTermCode +
            "&facultyPidm=" + $scope.selectedInstructor +
            "&courseTitle=" + $scope.courseTitle +
            "&creditFrom=" + $scope.creditFrom +
            "&creditTo=" + $scope.creditTo;
        $rootScope.getAPI($rootScope.middlewareServerUrl + searchURL, function (data) {
            if (data.CLASSLOOKUP.length == 0) {
                if (window.device) {
                    alert("No record found.");
                } else {
                    apprise("No record found.", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function (r) {

                    });
                }
            } else {
                $rootScope.classlookup = data.CLASSLOOKUP;
                $location.path("/app/LookupClasses/LookupClassesDetails");
            }
        });
    }
}

