             if (data.blockTranscriptsGrades[0].BLOCKTRANSCRIPTS == 'Y' || data.blockTranscriptsGrades[0].BLOCKGRADES == 'Y' ) {
                if (window.device) {
                    var doNothing = function() {
                        $rootScope.$apply(function() {
                            $location.path("/home");
                        });
                    };
                    navigator.notification.alert("Your transcript is not available due to holds on your record.", doNothing, 'Alert', 'Ok');
                } else {
                    apprise("Your transcript is not available due to holds on your record.", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {
                        $rootScope.$apply(function() {
                            $location.path("/home");
                        });
                    });
                }
             } else {
                $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.dropdownendpoint, function(data1) {
                    $scope.dropdown = data1;
/*                    if ($scope.dropdown.transcriptleveldropdown.length == 1) {
                        $scope.selectedvalue = $scope.dropdown.transcriptleveldropdown[0].LEVEL_CODE;
                        $scope.dropdownselect($scope.selectedvalue);
                    }*/
                });            
             }
        } else {
            $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.dropdownendpoint, function(data1) {
                $scope.dropdown = data1;
/*                if ($scope.dropdown.transcriptleveldropdown.length == 1) {
                    $scope.selectedvalue = $scope.dropdown.transcriptleveldropdown[0].LEVEL_CODE;
                    $scope.dropdownselect($scope.selectedvalue);
                }   */             
            });             
        }


    });

    $scope.dropdownselect = function(selectedvalue) {
        $('#termselect').blur();
        $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.listendpoint + $scope.selectedvalue, function(data) {
            $scope.listdata = data;
            $scope.creditselect();
        });
    }
    $scope.creditselect = function(selectedvalue) {
        $('#creditselect').blur();
        $scope.transfer = null;
        $scope.institution = null;
        $scope.transcripts = null;
        if ($scope.creditselectedvalue == "transfer") {
            var transfer = {
                attempted: 0,
                passed: 0,
                earned: 0,
                gpa: 0,
                credit: 0
            };
            transfer.attempted = angular.isUndefined($scope.listdata.transfer_data_total[0][0].ATTEMPTED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].ATTEMPTED_HOURS;
            transfer.passed = angular.isUndefined($scope.listdata.transfer_data_total[0][0].PASSED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].PASSED_HOURS;
            transfer.earned = angular.isUndefined($scope.listdata.transfer_data_total[0][0].EARNED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].EARNED_HOURS;
            transfer.gpa = angular.isUndefined($scope.listdata.transfer_data_total[0][0].GPA_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].GPA_HOURS;
            transfer.credit = angular.isUndefined($scope.listdata.transfer_data_total[0][0].QUALITY_POINTS) ? 0 : $scope.listdata.transfer_data_total[0][0].QUALITY_POINTS;
            console.log($scope.listdata.transfer_data[0][0].NODATAFOUND)
            $scope.transfer = transfer;
        } else if ($scope.creditselectedvalue == "institution") {
            $scope.institution = "data";
            var totalinst = {
                ah: 0,
                ph: 0,
                eh: 0,
                gpah: 0,
                qp: 0
            };
            angular.forEach($scope.listdata.institutioncredits, function(value, key) {
                this.ah += parseFloat(value.ATTEMPTED_HOURS);
                this.ph += parseFloat(value.SHRTCKG_PASS_HOURS);
                this.eh += parseFloat(value.SHRTCKG_EARN_HOURS);
                this.gpah += parseFloat(value.SHRTCKG_GPA_HOURS);
                this.qp += parseFloat(value.SHRTCKG_QUAL_PTS);
            }, totalinst);
            $scope.insttotal = totalinst;

        } else if ($scope.creditselectedvalue == "transcripts") {
            $scope.transcripts = "data";
            angular.forEach($scope.listdata.transcripttotal, function(value, key) {
                if (value.TRANSCRIPT_TYPE == "I") {
                    $scope.totalinst = value;
                } else if (value.TRANSCRIPT_TYPE == "T") {
                    $scope.totaltrans = value;
                } else if (value.TRANSCRIPT_TYPE == "Z") {
                    $scope.cumulative = value;
                    var GPATotal = parseFloat(value.QUALITY_POINTS) / parseFloat(value.GPA_HOURS);
                    value.GPA = GPATotal;

                }
            });
        }

    }
}

function pageprocessorRegistrationB1(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    var termdropdownURL = "/services/student/bannertermsForRegistrationLookup";
    $rootScope.getAPI($rootScope.middlewareServerUrl + termdropdownURL, function(data) {
        $scope.terms = data.termDropDown;
        //$scope.terms = [{"TERM_CODE": "201504","TERM_DESC":"Second Summer 5 week term 2015"}];

        if (angular.isArray($scope.terms) && $scope.terms.length > 0) {
            $scope.selectedvalue = $scope.terms[0].TERM_CODE;
            $scope.dropdownselect($scope.selectedvalue);
        }
    });

    $scope.lookedup = true;
    $scope.showaltpin = false;
    $scope.showregoptions = false;
    $scope.altpin = "";
    $scope.servererror = false;


    $scope.dropdownselect = function(term) {
        $('#termselect').blur();
        var termCode = $scope.selectedvalue;
        $scope.servererror = true; 

        if (termCode != "") {
            //Request student pin
            var pinUrl = "/services/student/banneraddlPinReqd/termCode=" + termCode;
            $rootScope.getAPI($rootScope.middlewareServerUrl + pinUrl, function(data) {
                if (data.error) {
                    //some issue with service.
                    $scope.servererror = true;
                    apprise("Internal server issue.", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {});  

                }  else if (data.addlPinReqd && data.addlPinReqd.length >= 1) {
                    $scope.servererror = false;
                    if (data.addlPinReqd[0].REQUIRED == "true") {
                        // Ask for student pin and validate
                        $scope.showaltpin = true;
                    } else {
                        $scope.showaltpin = false;
                    }
                } else {
                    $scope.servererror = false;
                    $scope.showaltpin = false;
                }

            });
        }        
    }

    $scope.validatePin = function() {
        if (!$scope.altpin) {
            apprise("Please enter PIN and retry !", {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {});   
            return;         
        } 
        if ( /^[0-9]*$/.test($scope.altpin) == false) {
            apprise("Only numeric values are allowed !", {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {});   
            return;              
        }
        
        var pinUrl = $rootScope.middlewareServerUrl + "/services/student/bannervalidateAddlPin";
        var postdata = "termCode=" + $scope.selectedvalue + "&pin=" + $scope.altpin;
        $rootScope.postAPI(pinUrl, postdata, function(data) {
            if (data.validateAddlPin.length >= 1) {
                if (data.validateAddlPin[0].SUCCESS == "true") {
                    $scope.showaltpin = false;
                } else {
                    apprise("The Pin you have entered is invalid. Please enter a valid Pin.", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {});
                }
            } else {
                apprise("The Pin you have entered is invalid. Please enter a valid Pin.", {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {});
            }
        });
    }


    $scope.openlookup = function() {
        if (!$scope.selectedvalue) {
            apprise("Please select a term for registration.", {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {
                return false;
            });
        } else {
            $scope.lookedup = false;
            var url = $rootScope.middlewareServerUrl + "/services/student/bannergetOtherClassSearchParams/termCode=" + $scope.selectedvalue;
            $rootScope.getAPI(url, function(data) {
                $scope.listdata = data;
                if (angular.isArray($scope.listdata.subjectDropDown) && $scope.listdata.subjectDropDown.length > 0) {
                    $scope.selectedSubject = $scope.listdata.subjectDropDown[0].SUBJECT_CODE;
                }
            });            
        }

    }

    $scope.lookup = function() {
        if (!$scope.selectedvalue) {
            apprise("Please select a term for registration.", {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {
                return false;
            });
        }  else {
            $location.url("/app/RegistrationB1/RegistrationB1Details?type=lookup&termCode=" + $scope.selectedvalue + "&subjCode=" + $scope.selectedSubject);
        }       
        
        /*?type=lookup&termCode=" + $scope.selectedvalue + "&subjCode=" + $scope.selectedSubject); */
    }

    $scope.current = function() {
        if (!$scope.selectedvalue) {
            apprise("Please select a term for registration.", {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {
                return false;
            });
        }  else {
            $location.url("/app/RegistrationB1/RegistrationB1Details?type=current&termCode=" + $scope.selectedvalue);
        }      
        

    }

    $scope.registerStudent = function() {
        if (!$scope.selectedvalue) {
            apprise("Please select a term for registration.", {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {
                return false;
            });
        }  else {
            if (!$scope.crn) {
                apprise("Please enter CRN", {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {});
                return;
            }
            var endpoint = $rootScope.middlewareServerUrl + "/services/student/bannerregisterStudent";
            var postdata = "termCode=" + $scope.selectedvalue + "&crn=" + $scope.crn;
           /*
            var data = {
                p_msg_out: "Registration request successfully processed."
            };
            */
            $rootScope.postAPI(endpoint, postdata, function (data) {
            if (data.p_msg_out) {
                apprise(data.p_msg_out, {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {
                    $rootScope.$apply(function() {
                        $location.path("/app/RegistrationB1/RegistrationB1");
                    });
                });
            } else if (data.error) {
                    apprise("Invalid CRN " + data.error, {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {
                        $rootScope.$apply(function() {
                            $location.path("/app/RegistrationB1/RegistrationB1");
                        });
                    });
                }
            });            
        }       

    }
}

function pageprocessorRegistrationB1Details(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    var type = $routeParams.type;
    var termCode = $routeParams.termCode;
    var subjCode = $routeParams.subjCode;
    $scope.termCode = termCode;
    $scope.loadCurrentClasses = function () {
        var url = $rootScope.middlewareServerUrl + "/services/student/bannerstudentDetailSchedule/termCode=" + termCode;
        $rootScope.getAPI(url, function (data) {
            if (data.scheduleCourseDetail && data.scheduleCourseDetail.length > 0) {
                $scope.scheduleCourseDetail = data.scheduleCourseDetail;
                angular.forEach($scope.scheduleCourseDetail, function (value, key) {
                    if (value.REG_CODE_DESC.indexOf('Drop') >= 0 || value.REG_CODE_DESC.indexOf('Delete') >= 0) {
                        value.DROPPABLE = false;
                        value.DESC_STYLE = {
                            color: "red"
                        }
                    } else {
                        value.DROPPABLE = true;
                        value.DESC_STYLE = {}
                    }
                });
            } else {
                apprise("No current classes could be found for selected term code", {
                    'verify': false,
                    'textYes': "Ok"
                }, function (r) {
                    $rootScope.$apply(function () {
                        $location.path("/app/RegistrationB1/RegistrationB1");
                    });
                });
            }
        });
    }
    if (type == "lookup") {
        $scope.isadd = true;
