        if (data.p_msg_out) {
            apprise(data.p_msg_out, {
                'verify': false,
                'textYes': "Ok"
            }, function (r) {
                $rootScope.$apply(function () {
                    $location.path("/app/RegistrationB1/RegistrationB1");
                });
            });
        }
        });
    }

    $scope.dropStudent = function (crn) {
        var endpoint = $rootScope.middlewareServerUrl + "/services/student/bannerdropStudent";
        var postdata = "termCode=" + $scope.termCode + "&crn=" + crn;
        /*var data = {
            p_msg_out: "Registration request successfully processed."
        };*/
        $rootScope.postAPI(endpoint, postdata, function (data) {
        if (data.p_msg_out) {
            apprise(data.p_msg_out, {
                'verify': false,
                'textYes': "Ok"
            }, function (r) {
                $scope.loadCurrentClasses();
