    }

    $scope.resetContact = function() {
        $rootScope.$apply(function() {
            $location.path("/app/AddUpdateB1/AddUpdateB1");
        });        
    }

    $scope.validateAddress = function() {
        //Validate city
        var city = $scope.address.CITY == null ? "" : $scope.address.CITY;
        /*if (!(city.search(/[^a-z. A-Z. ]+/) === -1)) {
            $scope.alert("Only alphabets and periods are allowed in City", "Address");
            return false;
        }*/
        if (city == "" || city.trim() == "") {
            $scope.alert("Please enter City", "Address");
            return false;
        }
        if ($scope.address.STREET_LINE1 == "" || $scope.address.STREET_LINE1 == null) {
            $scope.alert("Please enter Address line 1", "Address");
            return false;
        }
        var zip = $scope.address.ZIP == null ? "" : $scope.address.ZIP.trim();
        if ($scope.address.NATION_CODE == "" && zip == "" && $scope.address.STATE_CODE == "") {
            $scope.alert("State and Zip or Nation is required.", "Address");
            return false;
        }
        if (zip == "" && $scope.address.STATE_CODE != "") {
            $scope.alert("State and Zip or Nation is required.", "Address");
            return false;
        }
        if ($scope.address.NATION_CODE == "zz" && zip == "" && $scope.address.STATE_CODE == "") {
            $scope.alert("State and Zip is required.", "Address");
            return false;
        }
        if ($scope.address.primaryphone) {
            if ($scope.address.primaryphone.PHONE_NUMBER == '' || $scope.address.primaryphone.PHONE_NUMBER == null) {
                $scope.alert("Phone number is required.", "Address");
                return false;
            }            
        }
        var haveError = false;
        angular.forEach($scope.address.additionalphones, function(value,key) {
            if (value.PHONE_CODE == '' || value.PHONE_CODE ==  null) {
                haveError = true;
                $scope.alert("Telephone Type is required.","Address");
                return false;
            } else if (value.PHONE_NUMBER == '' || value.PHONE_NUMBER == null) {
                 haveError = true;
                $scope.alert("Telephone Number is required.","Address");
                return false;               
            }
        });
        if (haveError) { return false; }

        return true
    }


    $scope.addAddress = function() {
