function pageprocessorEvents(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    $.blockUI();
    var url = pagedef.url;
    if (window.device) {} else {
        url = "/websimulator/xml?url=" + pagedef.url;
    }


    var xhr = $.ajax({
        url: url,
        dataType: "xml",
        async: true,
        data: {},
        success: function(resp) {
            var results = $.xml2json(resp);

            var data = [];
            var index = 0;
            $.each(results.channel.item, function(key, val) {
                heading = (val.title).replace('&apos;',"'").replace('&amp;','&').replace('&lt;','<').replace('&gt;','>').replace('&quot;','"');
                index = index + 1;
                data.push({
                    "link": val.link,
                    "displaydate": val.pubDate,
                    "date": new Date(val.pubDate).getTime().toString(),
                    "title": heading,
                    "description": val.description,
                    "index": index
                });
            });
            $rootScope.Eventsdata = data;
            $("#appContent").append($(pagedef.pageTemplate));
            $("#EventsListData").eventCalendar({
                jsonData: data,
                showDescription: true,
                dataTemplate: pagedef.datatemplate
            });
            $.unblockUI();
        },
        error: function(e) {
            alert("Error : " + e);
            $.unblockUI();
            apprise("Error occured. Please try again later.", {
                "verify": false,
                "textYes": "Ok"
            }, function(r) {
                $window.history.back();
            });
        }
    });
